package com.aysekoc.questboard.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import org.hibernate.annotations.CreationTimestamp;import java.time.LocalDateTime;import java.util.HashSet;import java.util.Set;@Entity@Table(name = "users")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name= "username" , nullable = false, unique = true)    private String username;    @Column(name= "password", nullable = false)    private String password;    @Column(name="created_at", nullable = false)    @CreationTimestamp    private LocalDateTime createdAt;    @Enumerated(EnumType.STRING)    @Column(nullable = false)    private Role role = Role.USER;    @ManyToMany    @JoinTable(name = "user_task",            joinColumns = @JoinColumn(name = "user_id"),            inverseJoinColumns = @JoinColumn(name = "task_id")    )    @JsonIgnore    private Set<Task> userTask = new HashSet<>();}