package com.aysekoc.questboard.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import org.hibernate.annotations.CreationTimestamp;import java.time.LocalDateTime;import java.util.HashSet;import java.util.Set;@Entity@Table(name = "users")@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builderpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name= "username" , nullable = false, unique = true, length = 50)    private String username;    @Column(name= "email" ,nullable = false, unique = true)    private String email;    @Column(name= "password_hash", nullable = false)    private String passwordHash;    @Column(name="display_name", nullable = false)    private String displayName;    @Column(name="role", nullable = false)    private String role;    @Column(name="level", nullable = false)    private Integer level;    @Column(name="total_points", nullable = false)    private Integer totalPoints;    @Column(name="created_at", nullable = false)    @CreationTimestamp    private LocalDateTime createdAt;    @Column(name="difficulty")    @Enumerated(EnumType.STRING)    private Difficulty difficulty;    @ManyToMany    @JoinTable(name = "user_task",            joinColumns = @JoinColumn(name = "user_id"),            inverseJoinColumns = @JoinColumn(name = "task_id")    )    @JsonIgnore    private Set<Task> userTask = new HashSet<>();}